// Calculate comprehensive business metrics
const balance = $('Get DigiStore24 Balance').first().json.balance || 0;
const transactions = $('Get Last 30 Days Transactions').all();
const salesLog = $('Get Sales from Sheets').all();

// Revenue calculations
const totalRevenue = transactions.reduce((sum, t) => 
  sum + parseFloat(t.json.pay_amount || 0), 0
);
const totalCommission = transactions.reduce((sum, t) => 
  sum + parseFloat(t.json.commission || 0), 0
);
const salesCount = transactions.filter(t => 
  t.json.transaction_type === 'sale'
).length;

// Time-based metrics
const today = new Date();
const last7Days = transactions.filter(t => {
  const date = new Date(t.json.created_at);
  return (today - date) / (1000 * 60 * 60 * 24) <= 7;
});
const last30Days = transactions;

const revenue7d = last7Days.reduce((sum, t) => 
  sum + parseFloat(t.json.commission || 0), 0
);
const revenue30d = last30Days.reduce((sum, t) => 
  sum + parseFloat(t.json.commission || 0), 0
);

// Product performance
const productStats = {};
transactions.forEach(t => {
  const pid = t.json.product_id;
  if (!productStats[pid]) {
    productStats[pid] = {
      name: t.json.product_name,
      sales: 0,
      revenue: 0,
      commission: 0
    };
  }
  productStats[pid].sales++;
  productStats[pid].revenue += parseFloat(t.json.pay_amount || 0);
  productStats[pid].commission += parseFloat(t.json.commission || 0);
});

const topProducts = Object.entries(productStats)
  .sort((a, b) => b[1].commission - a[1].commission)
  .slice(0, 5)
  .map(([id, stats]) => ({
    productId: id,
    productName: stats.name,
    sales: stats.sales,
    commission: stats.commission.toFixed(2)
  }));

// Traffic source analysis
const sourceStats = {};
salesLog.forEach(s => {
  const source = s.json.utm_source || 'direct';
  if (!sourceStats[source]) {
    sourceStats[source] = { count: 0, revenue: 0 };
  }
  sourceStats[source].count++;
  sourceStats[source].revenue += parseFloat(s.json.commission || 0);
});

const topSources = Object.entries(sourceStats)
  .sort((a, b) => b[1].revenue - a[1].revenue)
  .slice(0, 3)
  .map(([source, stats]) => ({
    source: source,
    conversions: stats.count,
    revenue: stats.revenue.toFixed(2)
  }));

// Growth calculations
const dailyAverage = revenue30d / 30;
const weeklyAverage = revenue7d / 7;
const growthRate = weeklyAverage > 0 && dailyAverage > 0
  ? ((weeklyAverage - dailyAverage) / dailyAverage * 100).toFixed(2)
  : 0;

// Projections
const monthlyProjection = dailyAverage * 30;
const yearlyProjection = dailyAverage * 365;

// Payout eligibility
const payoutEligible = balance >= 50;
const payoutAmount = payoutEligible ? Math.floor(balance) : 0;

return {
  timestamp: new Date().toISOString(),
  balance: {
    current: balance.toFixed(2),
    payoutEligible: payoutEligible,
    payoutAmount: payoutAmount
  },
  revenue: {
    last7Days: revenue7d.toFixed(2),
    last30Days: revenue30d.toFixed(2),
    dailyAverage: dailyAverage.toFixed(2),
    weeklyAverage: weeklyAverage.toFixed(2),
    growthRate: growthRate,
    monthlyProjection: monthlyProjection.toFixed(2),
    yearlyProjection: yearlyProjection.toFixed(2)
  },
  sales: {
    total30Days: salesCount,
    last7Days: last7Days.length,
    conversionRate: salesLog.length > 0 
      ? (salesCount / salesLog.length * 100).toFixed(2) 
      : 0
  },
  topProducts: topProducts,
  topSources: topSources,
  performance: {
    grade: balance > 100 ? 'A+' : balance > 50 ? 'A' : balance > 25 ? 'B' : 'C',
    status: balance > 50 ? 'üî• Excellent' : balance > 25 ? '‚úÖ Good' : 'üìà Growing'
  }
};

